ScreenBackground::ScreenBackground (const BackgroundItemsList&          backgroundItemsList,
                                    const BackgroundItemGraphicsMap&    backgroundItemGraphicsMap)
:   m_backgroundItemsList(backgroundItemsList)
{
    // Set the screen's background item graphics.

    SetBackgroundItemGraphics (backgroundItemGraphicsMap);
} // Endproc.


ScreenBackground::~ScreenBackground ()
{
} // Endproc.


BackgroundItemsList ScreenBackground::GetBackgroundItemsList () const
{
    return (m_backgroundItemsList);
} // Endproc.


FolioStatus ScreenBackground::SetBackgroundItemGraphics (const BackgroundItemGraphicsMap& backgroundItemGraphicsMap)
{
    FolioStatus status = ERR_SUCCESS;

    // Set the graphic for each of the screen's background items.

    for (BackgroundItemsList::iterator itr = m_backgroundItemsList.begin ();
         (status == ERR_SUCCESS) && (itr != m_backgroundItemsList.end ());
         ++itr)
    {
        // Set the background item's graphic.
        
        status = itr->SetBackgroundItemGraphic (backgroundItemGraphicsMap);
    } // Endfor.

    return (status);
} // Endproc.


FolioStatus CreateScreenBackgrounds (const BackgroundItemGraphicsMap&   backgroundItemGraphicsMap,
                                     ScreenBackgroundsMap&              screenBackgroundsMap)
{
    FolioStatus status = ERR_SUCCESS;

    screenBackgroundsMap.clear ();   // Initialise!

    // Build the screen backgrounds map. 

    for (UInt32 index = 0; 
        (status == ERR_SUCCESS) && (index < (sizeof (g_screenBackgroundAttributesTable) / (sizeof (BackgroundItemsList)))); 
        ++index)
    {
        // Store the screen background in the screen backgrounds map.

        screenBackgroundsMap.insert (ScreenBackgroundsMap::value_type(g_screenBackgroundAttributesTable [index].m_screenNumber,
                                                                      new ScreenBackground(g_screenBackgroundAttributesTable [index].m_backgroundItemsList, 
                                                                                           backgroundItemGraphicsMap)));
    } // Endfor.

    if (status != ERR_SUCCESS)
    {
        screenBackgroundsMap.clear ();
    } // Endif.

    return (status);
} // Endproc.


FolioStatus QueryScreenBackgroundList (ScreenMap::ScreenNumber  screenNumber,
                                       BackgroundItemsList&     backgroundItemsList)
{
    FolioStatus status = ERR_SUCCESS;
    
    backgroundItemsList.clear ();   // Initialise!

    // Find the screen in the screen backgrounds map.

    ScreenBackgroundsMap::const_iterator    itr = g_screenBackgroundsMap.find (screenNumber);
    
    if (itr != g_screenBackgroundsMap.end ())
    {
        // Get the screen's background items list.

        backgroundItemsList = itr->second->GetBackgroundItemsList ();
    } // Endif.

    else
    {
        status = ERR_NOT_FOUND;
    } // Endelse.
    
    return (status);
} // Endproc.

